shader.fragment_init:


// v_tex0
// u_sampler0

// position

// time
// resolution

// colourStart
// colourProcess
// amplitudeColourR
// amplitudeColourG
// amplitudeColourB

// speedFlashR
// speedFlashG
// speedFlashB

// amplitudeFlashR
// amplitudeFlashG
// amplitudeFlashB

// mainSpeedX
// mainSpeedY
// mainSpeedZ

// mainAmplitudeX
// mainAmplitudeY
// mainAmplitudeZ

// mainStepX
// mainStepY
// mainStepZ

// mainIterations
// mainPower
// mainLight

uniform sampler2D backbuffer;
vec3 color = colourStart;

shader.fragment_main:


const float pi = 3.141592;
float fade = 0.0;
float gradient = 0.0;
float angle = 0.0;
float radius = 0.0;
float dist_from_center = length(position);
float angle_from_center = atan(position.y, position.x);		

float u=0.0;
float v=0.0;
float z=0.0;

//
// Circle tunnel
//
u = (mainStepX / dist_from_center) + time * mainSpeedX;
v = angle_from_center * mainSpeedZ;

fade = dist_from_center * mainIterations;
gradient = ((1.0 - pow(sin(u) + 1.0, mainPower)) + (1.0 - pow(sin(v) + 1.0, mainLight))) * fade;
color = vec3(gradient);
