#ifdef GL_ES
precision mediump float;
#endif

uniform float time;
uniform vec2 mouse;
uniform vec2 resolution;

const int numBalls = 32;
const float threshold = 0.001;

float ball(vec2 pos, float radius)
{
	float color = 0.0;
	float l = length(pos);
	if (l <= radius - threshold)
		color = 1.0;
	else if (l <= radius)
		color = (radius - l) / threshold;
	return color;
}

void main( void ) {
	float t = time * 0.25;

	vec2 position = (gl_FragCoord.xy / resolution.x) - vec2(0.5, 0.5 * resolution.y / resolution.x);

	float c = 0.0;
	vec3 color = vec3(0.0);
	for (int i=1; i<=numBalls; i++)
	{
		vec2 disp = vec2(sin(float(i*i) + t), cos(float(i*i*i) + t)) * 0.4;
		float radius = sin(float(i*i*i*i) + t) * 0.05 + 0.15;
		c = ball(position + disp, radius);
		color += vec3(c * (sin(float(i) * 0.5 + 0.5)), c * (sin(float(i*i)) * 0.5 + 0.5), c * (sin(float(i*i*i)) * 0.5 + 0.5));
	}

	gl_FragColor = vec4(color, 1.0);
}
